    private void calcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcularActionPerformed
        //Obteniendo las UV de las materias
        Double uvmat1 = 0.0;
        Double uvmat2 = 0.0;
        Double uvmat3 = 0.0;
        Double uvmat4 = 0.0;
        Double uvmat5 = 0.0;
        Double uvmat6 = 0.0;        
        
        String auxuvmat1 = (String)uvm1.getSelectedItem();
        if(auxuvmat1 != "Seleccione"){
            uvmat1 = Double.parseDouble(auxuvmat1);
        }else{
            uvmat1 = 0.0;
        }
                        
        String auxuvmat2 = (String)uvm2.getSelectedItem();
        if(auxuvmat2 != "Seleccione"){
            uvmat2 = Double.parseDouble(auxuvmat2);            
        }else{
            uvmat2 = 0.0;
        }
                        
        String auxuvmat3 = (String)uvm3.getSelectedItem();
        if(auxuvmat3 != "Seleccione"){
            uvmat3 = Double.parseDouble(auxuvmat3);
        }else{
            uvmat3 = 0.0;
        }        
        
        String auxuvmat4 = (String)uvm4.getSelectedItem();
        if(auxuvmat4 != "Seleccione"){
            uvmat4 = Double.parseDouble(auxuvmat4);
        }else{
            uvmat4 = 0.0;
        }                
        
        String auxuvmat5 = (String)uvm5.getSelectedItem();
        if(auxuvmat5 != "Seleccione"){
            uvmat5 = Double.parseDouble(auxuvmat5);
        }else{
            uvmat5 = 0.0;
        }        
        
        String auxuvmat6 = (String)uvm6.getSelectedItem();       
        if(auxuvmat6 != "Seleccione"){
            uvmat6 = Double.parseDouble(auxuvmat6);
        }else{
            uvmat6 = 0.0;            
        }                                   
        
        //Obteniendo notas y asignando estado de la materia
        Double not1 = 0.0;
        Double not2 = 0.0;
        Double not3 = 0.0;
        Double not4 = 0.0;
        Double not5 = 0.0;
        Double not6 = 0.0;
        
        Integer repmat1 = 0;
        Integer repmat2 = 0;
        Integer repmat3 = 0;
        Integer repmat4 = 0;
        Integer repmat5 = 0;
        Integer repmat6 = 0;
        
        Integer aprmat1 = 0;
        Integer aprmat2 = 0;
        Integer aprmat3 = 0;
        Integer aprmat4 = 0;
        Integer aprmat5 = 0;
        Integer aprmat6 = 0;        
        
        Integer invmat1 = 0;
        Integer invmat2 = 0;
        Integer invmat3 = 0;
        Integer invmat4 = 0;
        Integer invmat5 = 0;
        Integer invmat6 = 0;
        
        if(n1.getText().length()==0){ //Si el nota está vacio
            not1 = 0.0;
        }else{
            String nota1 = n1.getText();
            not1 = Double.parseDouble(nota1);
            if(not1 >= 6.0 && not1 <= 10.0){
                aprmat1 = 1;
                repmat1 = 0;
                invmat1 = 0;
            }else if(not1 >= 0.0 && not1 <= 5.9){
                aprmat1 = 0;
                repmat1 = 1;
                invmat1 = 0;
            }else{
                not1 = 0.0;
                aprmat1 = 0;
                repmat1 = 0;
                invmat1 = 1;
            }            
        }                                
        
        if(n2.getText().length()==0){
            not2 = 0.0;
        }else{
            String nota2 = n2.getText();
            not2 = Double.parseDouble(nota2);
            if(not2 >= 6.0 && not2 <= 10.0){
                aprmat2 = 1;
                repmat2 = 0;
                invmat2 = 0;
            }else if(not2 >= 0.0 && not2 <= 5.9){       
                aprmat2 = 0;
                repmat2 = 1;
                invmat2 = 0;
            }else{
                not2 = 0.0;
                aprmat2 = 0;
                repmat2 = 0;
                invmat2 = 1;
            }            
        }
        
        if(n3.getText().length()==0){
            not3 = 0.0;
        }else{
            String nota3 = n3.getText();
            not3 = Double.parseDouble(nota3);
            if(not3 >= 6.0 && not3 <= 10.0){
                aprmat3 = 1;
                repmat3 = 0;
                invmat3 = 0;
            }else if(not3 >= 0.0 && not3 <= 5.9){        
                aprmat3 = 0;
                repmat3 = 1;
                invmat3 = 0;
            }else{
                not3 = 0.0;
                aprmat3 = 0;
                repmat3 = 0;
                invmat3 = 1;
            }            
        }
        
        if(n4.getText().length()==0){
            not4 = 0.0;
        }else{
            String nota4 = n4.getText();
            not4 = Double.parseDouble(nota4);
            if(not4 >= 6.0 && not4 <= 10.0){
                aprmat4 = 1;
                repmat4 = 0;
                invmat4 = 0;
            }else if(not4 >= 0.0 && not4 <= 5.9){
                aprmat4 = 0;
                repmat4 = 1;
                invmat4 = 0;
            }else{
                not4 = 0.0;
                aprmat4 = 0;
                repmat4 = 0;
                invmat4 = 1;
            }            
        }
        
        if(n5.getText().length()==0){
            not5 = 0.0;
        }else{
            String nota5 = n5.getText();
            not5 = Double.parseDouble(nota5);
            if(not5 >= 6.0 && not5 <= 10.0){
                aprmat5 = 1;
                repmat5 = 0;
                invmat5 = 0;
            }else if(not5 >= 0.0 && not5 <= 5.9){  
                aprmat5 = 0;
                repmat5 = 1;
                invmat5 = 0;
            }else{
                not5 = 0.0;
                aprmat5 = 0;
                repmat5 = 0;
                invmat5 = 1;
            }            
        }
        
        if(n6.getText().length()==0){
            not6 = 0.0;
        }else{
            String nota6 = n6.getText();
            not6 = Double.parseDouble(nota6);
            if(not6 >= 6.0 && not6 <= 10.0){
                aprmat6 = 1;
                repmat6 = 0;
                invmat6 = 0;
            }else if(not6 >= 0.0 && not6 <= 5.9){
                aprmat6 = 0;
                repmat6 = 1;
                invmat6 = 0;
            }else{
                not6 = 0.0;
                aprmat6 = 0;
                repmat6 = 0;
                invmat6 = 1;
            }            
        }
        
        //Unidades de mérito
        Double ume1 = 0.0;
        Double ume2 = 0.0;
        Double ume3 = 0.0;
        Double ume4 = 0.0;
        Double ume5 = 0.0;
        Double ume6 = 0.0;                                 
        
        if(not1 >= 6.0 && not1 <= 10.0){
            ume1 = not1 * uvmat1;
        }else{
            ume1 = 0.0;
            not1 = 0.0;
            uvmat1 = 0.0;
        }
        
        if(not2 >= 6.0 && not2 <= 10.0){
            ume2 = not2 * uvmat2;
        }else{
            ume2 = 0.0;
            not2 = 0.0;
            uvmat2 = 0.0;
        }
        
        if(not3 >= 6.0 && not3 <= 10.0){
            ume3 = not3 * uvmat3;
        }else{
            ume3 = 0.0;
            not3 = 0.0;
            uvmat3 = 0.0;
        }
        
        if(not4 >= 6.0 && not4 <= 10.0){
            ume4 = not4 * uvmat4;
        }else{
            ume4 = 0.0;
            not4 = 0.0;
            uvmat4 = 0.0;
        }
        
        if(not5 >= 6.0 && not5 <= 10.0){
            ume5 = not5 * uvmat5;
        }else{
            ume5 = 0.0;
            not5 = 0.0;
            uvmat5 = 0.0;
        }
        
        if(not6 >= 6.0 && not6 <= 10.0){
            ume6 = not6 * uvmat6;
        }else{
            ume6 = 0.0;
            not6 = 0.0;
            uvmat6 = 0.0;
        }
        
        //Validacion de elementos vacios
        //Caja de nombre
        String auxnombre = "vacio";
        
        if(nombre.getText().length()==0){
            auxnombre = "vacio";
        }else{
            auxnombre = nombre.getText();
        }
        
        //Seleccion de carrera
        String auxcarrera = (String)carrera.getSelectedItem();
        
        if(auxcarrera == "Seleccione"){
            auxcarrera = "vacio";
        }else{
            auxcarrera = "lleno";
        }
        
        //Seleccion del año
        String auxanio = (String)anio.getSelectedItem();
        
        if(auxanio == "Seleccione"){
            auxanio = "vacio";
        }else{
            auxanio = "lleno";
        }
        
        //Seleccion del ciclo
        String auxciclo = (String)ciclo.getSelectedItem();
        
        if(auxciclo == "Seleccione"){
            auxciclo = "vacio";
        }else{
            auxciclo = "lleno";
        }
        
        //Seleccion de materias (CURSADAS Y NO CURSADAS)
        String auxmat1 = (String)mat1.getSelectedItem();
        String auxmat2 = (String)mat2.getSelectedItem();
        String auxmat3 = (String)mat3.getSelectedItem();
        String auxmat4 = (String)mat4.getSelectedItem();
        String auxmat5 = (String)mat5.getSelectedItem();
        String auxmat6 = (String)mat6.getSelectedItem();
        
        Integer noCursomat1 = 0;
        Integer noCursomat2 = 0;
        Integer noCursomat3 = 0;
        Integer noCursomat4 = 0;
        Integer noCursomat5 = 0;
        Integer noCursomat6 = 0;
                
        if(auxmat1 == "Seleccione" || invmat1 == 1 || auxmat1 == "Llene los campos anteriores" || auxuvmat1 == "Seleccione" || n1.getText().length()==0){
            ume1 = 0.0;
            not1 = 0.0;
            uvmat1 = 0.0;
            aprmat1 = 0;
            repmat1 = 0;
            noCursomat1 = 1;
        }
        
        if(auxmat2 == "Seleccione" || invmat2 == 1 || auxmat2 == "Llene los campos anteriores" || auxuvmat2 == "Seleccione" || n2.getText().length()==0){
            ume2 = 0.0;
            not2 = 0.0;
            uvmat2 = 0.0;
            aprmat2 = 0;
            repmat2 = 0;
            noCursomat2 = 1;
        }
        
        if(auxmat3 == "Seleccione" || invmat3 == 1 || auxmat3 == "Llene los campos anteriores" || auxuvmat3 == "Seleccione" || n3.getText().length()==0){
            ume3 = 0.0;
            not3 = 0.0;
            uvmat3 = 0.0;
            aprmat3 = 0;
            repmat3 = 0;
            noCursomat3 = 1;
        }
        
        if(auxmat4 == "Seleccione" || invmat4 == 1 || auxmat4 == "Llene los campos anteriores" || auxuvmat4 == "Seleccione" || n4.getText().length()==0){
            ume4 = 0.0;
            not4 = 0.0;
            uvmat4 = 0.0;
            aprmat4 = 0;
            repmat4 = 0;
            noCursomat4 = 1;
        }
        
        if(auxmat5 == "Seleccione" || invmat5 == 1 || auxmat5 == "Llene los campos anteriores" || auxuvmat5 == "Seleccione" || n5.getText().length()==0){
            ume5 = 0.0;
            not5 = 0.0;
            uvmat5 = 0.0;
            aprmat5 = 0;
            repmat5 = 0;
            noCursomat5 = 1;
        }
        
        if(auxmat6 == "Seleccione" || auxmat6 == "Solo para Ing. Agronegocios (2° y 4° año, Ciclo 02)" || invmat6 == 1 || auxmat6 == "Llene los campos anteriores" || auxuvmat6 == "Seleccione" || n6.getText().length()==0){
            ume6 = 0.0;
            not6 = 0.0;
            uvmat6 = 0.0;
            aprmat6 = 0;
            repmat6 = 0;
            noCursomat6 = 1;
        }
        
        //CUM               
        Double resCum = 0.0;                                      
        String detalleNom = "";
        String detalleRes = "";
        String detalleResCum = "";
        
        resCum = ((ume1 + ume2 + ume3 + ume4 + ume5 + ume6) / (uvmat1 + uvmat2 + uvmat3 + uvmat4 + uvmat5 + uvmat6));
        resCum = Math.round((resCum)* Math.pow(10, 1))/Math.pow(10, 1);
        
        String mostrarCum = String.valueOf(resCum);                                  
        
        if(auxnombre == "vacio" || auxcarrera == "vacio" || auxanio == "vacio" || auxciclo == "vacio"){
            mostrarCum = "ERROR";
            cum.setText(mostrarCum);
            detalleCum.setText("CUM: ERROR");
            
            detalleRes = "SELECCIONE LAS OPCIONES O INGRESE LOS DATOS CORRECTAMENTE";
            detalleResult.setText(detalleRes);            
                       
        }else{                                               
            if(mostrarCum == "NaN"){
                mostrarCum = "0.0";
                cum.setText(mostrarCum);
            }else{
                cum.setText(mostrarCum);
            }                    
            
            //DETALLE DE RESULTADOS
            //Asignando estados
            if(aprmat1 == 1 && repmat1 == 0 && noCursomat1 == 0){
                sta1.setText("APROBADA");
            }else if(aprmat1 == 0 && repmat1 == 1 && noCursomat1 == 0){
                sta1.setText("REPROBADA");
            }else{
                sta1.setText("NO CURSADA / INVÁLIDA");
            }
            
            if(aprmat2 == 1 && repmat2 == 0 && noCursomat2 == 0){
                sta2.setText("APROBADA");
            }else if(aprmat2 == 0 && repmat2 == 1 && noCursomat2 == 0){
                sta2.setText("REPROBADA");
            }else{
                sta2.setText("NO CURSADA / INVÁLIDA");
            }
            
            if(aprmat3 == 1 && repmat3 == 0 && noCursomat3 == 0){
                sta3.setText("APROBADA");
            }else if(aprmat3 == 0 && repmat3 == 1 && noCursomat3 == 0){
                sta3.setText("REPROBADA");
            }else{
                sta3.setText("NO CURSADA / INVÁLIDA");
            }
            
            if(aprmat4 == 1 && repmat4 == 0 && noCursomat4 == 0){
                sta4.setText("APROBADA");
            }else if(aprmat4 == 0 && repmat4 == 1 && noCursomat4 == 0){
                sta4.setText("REPROBADA");
            }else{
                sta4.setText("NO CURSADA / INVÁLIDA");
            }
            
            if(aprmat5 == 1 && repmat5 == 0 && noCursomat5 == 0){
                sta5.setText("APROBADA");
            }else if(aprmat5 == 0 && repmat5 == 1 && noCursomat5 == 0){
                sta5.setText("REPROBADA");
            }else{
                sta5.setText("NO CURSADA / INVÁLIDA");
            }
            
            if(aprmat6 == 1 && repmat6 == 0 && noCursomat6 == 0){
                sta6.setText("APROBADA");
            }else if(aprmat6 == 0 && repmat6 == 1 && noCursomat6 == 0){
                sta6.setText("REPROBADA");
            }else{
                sta6.setText("NO CURSADA / INVÁLIDA");
            }
            
            //Mostrando resultados            
            Integer totalAprobadas = 0;
            Integer totalReprobadas = 0;
            Integer totalCursadas = 0;
            Integer totalNoCursadas = 0;

            totalNoCursadas = noCursomat1 + noCursomat2 + noCursomat3 + noCursomat4 + noCursomat5 + noCursomat6;
            totalAprobadas = aprmat1 + aprmat2 + aprmat3 + aprmat4 + aprmat5 + aprmat6;
            totalReprobadas = repmat1 + repmat2 + repmat3 + repmat4 + repmat5 + repmat6;
            totalCursadas = totalAprobadas + totalReprobadas;
            
            detalleNom = auxnombre + ".";
            detalleRes = "Su estado actual es: " + totalCursadas + " materias cursadas, " + totalAprobadas + " aprobadas, " + totalReprobadas + " reprobadas, " + totalNoCursadas +  " no cursadas o inválidas.";
            detalleResCum = mostrarCum;
            
            detalleNombre.setText("Resultados de: " + detalleNom);
            detalleResult.setText(detalleRes);
            detalleCum.setText("Su CUM actual es de: " + detalleResCum);            
        }                        
        
    }//GEN-LAST:event_calcularActionPerformed

